import React, { useState, useEffect } from 'react';
import './index.css'


const App = () => {

  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  // Määritellään käsittelija napille 1 
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Tapahtuman aiheutti: ", event.target);
    console.log("Hakusana: ", query);
  };

  // Määritellään käsittelija napille 2 
  const handleClick = (event) => {
    event.preventDefault();
    console.log("Tapahtuman aiheutti: ", event.target);
  };

  //Haetaan kaikki elokuvat
  const GetMovieData = () => {
    fetch("http://localhost:8081/api/getall")
      .then((results) => {
        return results.json();
      })
      .then((data) => {
        console.log(data);
        const items = data;

        setResults(items)
      });
  };

  // Komponentin palauttama JSX muotoinen esitys
  return (
    <div>
      <h1>Hakusivu</h1>
      <div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Hae: </label>
            <input
              type="search"
              value={query}
              onChange={(event) => setQuery(event.target.value)}
              className="form-control"
              placeholder="Syötä hakutermi"
              name="query"
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Submit
              </button>

            <button
              type="button"
              className="btn"
              onClick={handleClick}
            >
              Hae kaikki
              </button>
          </div>
        </form>
      </div>
    </div>
  );
};
ReactDOM.render(<App />, document.getElementById("root"));
